let audio = new Audio(); // Создаём новый элемент Audio
// пустой звук
audio.src =
  "data:audio/mpeg;base64,";
audio.autoplay = true; // Автоматически запускаем

export function soundPlay(src) {
  audio.src = src;
  try {
    audio.volume = 0.2;
    audio.play();
  } catch (e) {
    console.log("Звук пользователь, нажми на экран.");
  }
}
